function burgerMenu(){const e=document.querySelector(".burger"),t=document.querySelector(".menu"),i=document.querySelector("body");e.addEventListener("click",(()=>{t.classList.contains("active")?(t.classList.remove("active"),e.classList.remove("active"),i.classList.remove("locked")):(t.classList.add("active"),e.classList.add("active"),i.classList.add("locked"))})),window.addEventListener("resize",(()=>{window.innerWidth>991.98&&(t.classList.remove("active"),e.classList.remove("active"),i.classList.remove("locked"))}))}burgerMenu();const textField=document.querySelector(".text-field-navigation-cont__text-field"),textFieldIcon=document.querySelector(".text-field-icon");textField.addEventListener("mouseover",(function(){textFieldIcon.classList.add("dis-active")})),textField.addEventListener("mouseout",(function(){textFieldIcon.classList.remove("dis-active")}));