function burgerMenu(){const t=document.querySelector(".burger"),e=document.querySelector(".menu"),n=document.querySelector("body");t.addEventListener("click",(()=>{e.classList.contains("active")?(e.classList.remove("active"),t.classList.remove("active"),n.classList.remove("locked")):(e.classList.add("active"),t.classList.add("active"),n.classList.add("locked"))})),window.addEventListener("resize",(()=>{window.innerWidth>991.98&&(e.classList.remove("active"),t.classList.remove("active"),n.classList.remove("locked"))}))}burgerMenu();const langEngButton=document.getElementById("langEng"),langUkrButton=document.getElementById("langUkr"),langButtonCont=document.querySelector(".language-cont");langButtonCont.addEventListener("click",(function(t){t.target==langUkrButton&&(langUkrButton.classList.add("active-button"),langEngButton.classList.remove("active-button")),t.target==langEngButton&&(langEngButton.classList.add("active-button"),langUkrButton.classList.remove("active-button"))}));